//a custom hook that creates the socket.io-client, exposes a tweet object, and whenever a new tweet is received - will add to the array of all tweets
import React, { useState, useEffect } from "react";
import {io} from "socket.io-client";
const ENDPOINT = "http://localhost:4001/";

function useTweets() {
    //received tweets from server  
    const [tweets, setTweets] = useState("");

    //create WebSocket connection with server
    const socket = io(ENDPOINT , {
        withCredentials: true,
        extraHeaders: {
            "my-custom-header": "abcd"
        }
    });

    //handle tweet events
    useEffect(() => {
        socket.on("tweet", (tweet) => {
            console.log(tweet)
            //setTweets(tweet.data.text);
            setTweets(tweet)
        });

        return () => socket.disconnect();
    }, []);

    return tweets;
}

export default useTweets;
