import $ from 'jquery'

const tf = require('@tensorflow/tfjs');

let model, metadata;

//urls for pre-trained sentiment model
const urls = {
    model: 'https://storage.googleapis.com/tfjs-models/tfjs/sentiment_cnn_v1/model.json',
    metadata: 'https://storage.googleapis.com/tfjs-models/tfjs/sentiment_cnn_v1/metadata.json'
};

const SentimentThreshold = {
    Positive: 0.66,
    Neutral: 0.33,
    Negative: 0
}

const PAD_INDEX = 0;
const OOV_INDEX = 2;

//1) LOAD SENTIMENT MODEL
async function loadModel(url) {
    try {
        const model = await tf.loadLayersModel(url);
        return model;
    } catch (err) {
        console.log(err);
    }
}
 
async function loadMetadata(url) {
    try {
        const metadataJson = await fetch(url);
        const metadata = await metadataJson.json();
        return metadata;
    } catch (err) {
        console.log(err);
    }
}

//2) PERFORM SENTIMENT ANALYSIS ON EACH TWEET
async function setupSentimentModel(){
    if(typeof model === 'undefined'){
        model = await loadModel(urls.model);
    }
    if(typeof metadata === 'undefined'){
        metadata = await loadMetadata(urls.metadata);
    }
}

//takes in array of tweets, gets sentiment score, determine if positive, neutral, or negative
function processTwitterData(tweets){
    setupSentimentModel().then(
        result => {
            const twitterData = [];
            $.each(tweets, function( index, tweet ) {
                //console.log("tweet.text: " + tweet.text);

                const tweet_text = tweet.text.replace(/(?:https?|ftp):\/\/[\n\S]+/g, '');
                const sentiment_score = getSentimentScore(tweet_text);

                console.log("sentiment_score: " + sentiment_score);
                let tweet_sentiment = '';
                if(sentiment_score > SentimentThreshold.Positive){
                    tweet_sentiment = 'positive'
                }else if(sentiment_score > SentimentThreshold.Neutral){
                    tweet_sentiment = 'neutral'
                }else if(sentiment_score >= SentimentThreshold.Negative){
                    tweet_sentiment = 'negative'
                }
                
                //console.log("tweet_sentiment: " + tweet_sentiment);

                twitterData.push({
                    sentiment: tweet_sentiment,
                    score: sentiment_score.toFixed(4),
                    tweet: tweet_text
                });
            });
            //console.log("twitterData: " + twitterData);
            $('.spinner-border').addClass('d-none');
            displayTweets(twitterData.filter(t => t.sentiment == 'positive'), 'positive');
            displayTweets(twitterData.filter(t => t.sentiment == 'neutral'), 'neutral');
            displayTweets(twitterData.filter(t => t.sentiment == 'negative'), 'negative');
            $('#tweet-list').removeClass('d-none');
            displayPieChart(twitterData);
        }
    )   
}

//gets the sentiment score of a single tweet using model.predict()
function getSentimentScore(text) {
    const inputText = text.trim().toLowerCase().replace(/(\.|\,|\!)/g, '').split(' ');
    // Convert the words to a sequence of word indices.
    const sequence = inputText.map(word => {
      let wordIndex = metadata.word_index[word] + metadata.index_from;
      if (wordIndex > metadata.vocabulary_size) {
        wordIndex = OOV_INDEX;
      }
      return wordIndex;
    });
    // Perform truncation and padding.
    const paddedSequence = padSequences([sequence], metadata.max_len);
    const input = tf.tensor2d(paddedSequence, [1, metadata.max_len]);
 
    const predictOut = model.predict(input);
    const score = predictOut.dataSync()[0];
    predictOut.dispose();
 
    return score;
}

function padSequences(sequences, maxLen, padding = 'pre', truncating = 'pre', value = PAD_INDEX) {
    return sequences.map(seq => {
      if (seq.length > maxLen) {
        if (truncating === 'pre') {
          seq.splice(0, seq.length - maxLen);
        } else {
          seq.splice(maxLen, seq.length - maxLen);
        }
      }
  
      if (seq.length < maxLen) {
        const pad = [];
        for (let i = 0; i < maxLen - seq.length; ++i) {
          pad.push(value);
        }
        if (padding === 'pre') {
          seq = pad.concat(seq);
        } else {
          seq = seq.concat(pad);
        }
      }
  
      return seq;
    });
  }

//function to run the sentiment analysis
export function twitterSentiment(tweets) {
    processTwitterData(tweets);
}

