import React from "react";
import useTweets from "../utils/useTweets"
import { useState, useRef, useEffect } from "react";
import axios from 'axios'; //to make api calls
import {Form, FormGroup, Button} from "react-bootstrap"

export const HomePage = () => {
	const [rules, setRules] = useState(""); //state var to set the rules for tweet stream
    const tweets = useTweets() // useTweets() is a hook that will update the 'tweets' array with new tweets as soon as it is received from the socket

	//function to handle form submission event
	const handleSubmit = (evt) => {
		evt.preventDefault();
		alert(`Submitting Rule ${rules}`)

		// axios.post("http://localhost:4001/", {
		// 	rules: rules
		// });
		axios.post("/", {
			rules: rules
		}).then().catch((err) => {
			console.log("error");
		});

	}

    return (
		//print out list of tweets in real-time as they are updated by useTweets hook
		<div>
			<div class="container">

			<Form>
  <Form.Group className="mb-3" controlId="formBasicEmail">
    <Form.Label>Email address</Form.Label>
    <Form.Control type="email" placeholder="Enter email" />
    <Form.Text className="text-muted">
      We'll never share your email with anyone else.
    </Form.Text>
  </Form.Group>

  <Form.Group className="mb-3" controlId="formBasicPassword">
    <Form.Label>Password</Form.Label>
    <Form.Control type="password" placeholder="Password" />
  </Form.Group>
  <Form.Group className="mb-3" controlId="formBasicCheckbox">
    <Form.Check type="checkbox" label="Check me out" />
  </Form.Group>
  <Button variant="primary" type="submit">
    Submit
  </Button>
</Form>

				<form onSubmit={handleSubmit}>  {/* when onSubmit event happens, call the handleSubmit function */}
					<FormGroup className="mb-3">
						<Form.Label>
							Rule:
							<input type="text" value={rules} onChange={e => setRules(e.target.value)} /> {/* value is the state rules, onChange event will change the state to value submitted*/}
						</Form.Label>
					</FormGroup>
					
					
					
					{/* <label>
						Rule:
						<input type="text" value={rules} onChange={e => setRules(e.target.value)} /> 
					</label>
					<input type="submit" value="Submit" /> */}

				</form>

				<div id="tweetStream">
					{tweets.map((tweet) => (
						<div class ="card my-4">
						<div class="card-body">
							<h5 class="card-title">{tweet.text}</h5>
							<h6 class="card-subtitle mb-2 text-muted">{tweet.username}</h6>

							<a class="btn btn-primary mt-3" href={"https://twitter.com/" + tweet.username + "/status/" + tweet.id} target="_blank">
								Go To Tweet
							</a>
						</div>
						</div>
					))}
				</div>
			</div>
		</div>
	);
};
